import { React, useState, useRef } from "react";
import './App.css';
import logoImage from "./data/UOH_Database_logo_mini.png";
import { Lecture } from "./components/Lecture"
import { SyllabusPage } from "./components/SyllabusPage";
import { SyllabusSearch } from "./components/SyllabusSearch";
import Syllabus from "./data/syllabus_map.json";


export const App = () => {
  const [searchText, setSearchText] = useState("");
  const textRef = useRef(null)
  // チェックボックス
  const initialVal = { common: false, sis: false, em: false, emgbc: false, eng:false, sci:false, shse:false, cnas:false, minor:false, 1:false, 2:false, 3:false, 4:false, first:false, second:false};
  const [val, setVal] = useState(initialVal);

  // JSONデータ
  const data = Syllabus;
  // 検索ボタン
  const onClickSearch = () => {
    setSearchText(textRef.current.value);
  };

  const handleChange = e => {
    const newVal = Object.assign({}, val, {
      [e.target.value]: !val[e.target.value]
    });
    setVal(newVal);
  };

  return (
    <>
      <SyllabusSearch />
    </>
  )
};
