import { React, useState, useRef } from "react";
import './SyllabusSearch.css';
import logoImage from "../../data/UOH_Database_logo_mini.png";
import { Lecture } from "./Lecture"
import Syllabus from "../../data/syllabus_map.json";


export const SyllabusSearch = () => {
  const [searchText, setSearchText] = useState("");
  const textRef = useRef(null)
  // チェックボックス
  const initialVal = { common: false, sis: false, em: false, emgbc: false, eng:false, sci:false, shse:false, cnas:false, minor:false, 1:false, 2:false, 3:false, 4:false, first:false, second:false};
  const [val, setVal] = useState(initialVal);

  // JSONデータ
  const data = Syllabus;
  // 検索ボタン
  const onClickSearch = () => {
    setSearchText(textRef.current.value);
  };

  const onClickReset = () => {
    setSearchText("")
  };

  const handleChange = e => {
    const newVal = Object.assign({}, val, {
      [e.target.value]: !val[e.target.value]
    });
    setVal(newVal);
  };

  return (
    <>
      <div className="header">
        <h1>UOH DATABASE</h1>
      </div>
      <div className="search-form">
        <div className="check-box-1">
          <label>
            <input
              type="checkbox"
              value="common"
              onChange={handleChange}
              checked={val['common']}
            />
            全学共通科目
          </label>
          <label>
            <input
              type="checkbox"
              value="sis"
              onChange={handleChange}
              checked={val['sis']}
            />
            社会情報科学部
          </label>
          <label>
            <input
              type="checkbox"
              value="em"
              onChange={handleChange}
              checked={val['em']}
            />
            国際商経学部
          </label>
          <label>
            <input
              type="checkbox"
              value="emgbc"
              onChange={handleChange}
              checked={val['emgbc']}
            />
            国際商経GCB
          </label>
          <label>
            <input
              type="checkbox"
              value="eng"
              onChange={handleChange}
              checked={val['eng']}
            />
          工学部
          </label>
          <label>
            <input
              type="checkbox"
              value="sci"
              onChange={handleChange}
              checked={val['sci']}
            />
            理学部
          </label>
          <label>
            <input
              type="checkbox"
              value="shse"
              onChange={handleChange}
              checked={val['shse']}
            />
          環境人間学部
          </label>
          <label>
            <input
              type="checkbox"
              value="cnas"
              onChange={handleChange}
              checked={val['cnas']}
            />
            看護学部
          </label>
          <label>
            <input
              type="checkbox"
              value="minor"
              onChange={handleChange}
              checked={val['minor']}
            />
          副専攻
          </label>
        </div>
        <div className="year">
          <label>
            <input
              type="checkbox"
              value="1"
              onChange={handleChange}
              checked={val["1"]}
            />
          1年次
          </label>
          <label>
            <input
              type="checkbox"
              value="2"
              onChange={handleChange}
              checked={val["2"]}
            />
          2年次
          </label>
          <label>
            <input
              type="checkbox"
              value="3"
              onChange={handleChange}
              checked={val["3"]}
            />
          3年次
          </label>
          <label>
            <input
              type="checkbox"
              value="4"
              onChange={handleChange}
              checked={val["4"]}
            />
          4年次
          </label>
        </div>
        <div className="term">
          <label>
            <input
              type="checkbox"
              value="first"
              onChange={handleChange}
              checked={val["first"]}
            />
          前期
          </label>
          <label>
            <input
              type="checkbox"
              value="second"
              onChange={handleChange}
              checked={val["second"]}
            />
          後期
          </label>
        </div>
        <div className="input-area">
          <input ref={textRef} placeholder=" 科目名を部分一致検索" className="text-input" />
          <button className="input-button" onClick={onClickSearch}>検索</button>
          <button className="input-button" onClick={onClickReset}>検索</button>
        </div>

      </div>
      <table>
        <thead>
          <tr>
            <th className="lecture-name">科目名</th><th>開講年次・学期</th><th>担当教員</th><th>科目番号</th>
          </tr>
        </thead>
        <tbody>
          {Object.keys(data).map(key => <Lecture key={key} details={data[key]} filterClass={val} searchText={searchText} />)}
        </tbody>
    </table>
    </>
  )
};
