import { React, useState } from "react";
import './App.css';
import { SearchForm } from "./components/SearchForm";
import { Lecture } from "./components/Lecture"
import Syllabus from "./data/syllabus_map.json";

export const App = () => {
  const [searchText, setSearchText] = useState("");
  const onChangeSearchText = (event) => setSearchText(event.target.value);

  // チェックボックス
  const initialVal = { common: false, sis: false, em: false, emgbc: false, eng:false, sci:false, shse:false, cnas:false, minor:false };
  const [val, setVal] = useState(initialVal);

  // JSONデータ
  const data = Syllabus;
  // 検索ボタン
  const onClickSearch = () => {
    if (searchText === "") return;
    console.log(searchText);
  };

  const handleChange = e => {
    const newVal = Object.assign({}, val, {
      [e.target.value]: !val[e.target.value]
    });
    setVal(newVal);
  };

  return (
    <>
      <div>
        <label>
          <input
            type="checkbox"
            value="common"
            onChange={handleChange}
            checked={val['common']}
          />
          全学共通科目
        </label>
        <label>
          <input
            type="checkbox"
            value="sis"
            onChange={handleChange}
            checked={val['sis']}
          />
          社会情報科学部
        </label>
        <label>
          <input
            type="checkbox"
            value="em"
            onChange={handleChange}
            checked={val['em']}
          />
          国際商経学部
        </label>
        <label>
          <input
            type="checkbox"
            value="emgbc"
            onChange={handleChange}
            checked={val['emgbc']}
          />
          国際商経GCB
        </label>
        <label>
          <input
            type="checkbox"
            value="eng"
            onChange={handleChange}
            checked={val['eng']}
          />
        工学部
        </label>
        <label>
          <input
            type="checkbox"
            value="sci"
            onChange={handleChange}
            checked={val['sci']}
          />
          理学部
        </label>
        <label>
          <input
            type="checkbox"
            value="shse"
            onChange={handleChange}
            checked={val['shse']}
          />
        環境人間学部
        </label>
        <label>
          <input
            type="checkbox"
            value="cnas"
            onChange={handleChange}
            checked={val['cnas']}
          />
          看護学部
        </label>
        <label>
          <input
            type="checkbox"
            value="minor"
            onChange={handleChange}
            checked={val['minor']}
          />
        副専攻
        </label>
      </div>
      <SearchForm
        searchText={searchText}
        onChange={onChangeSearchText}
        onClick={onClickSearch}
      />
      <p>{searchText}</p>
      <table>
        <tr>
          <th>科目名</th><th>開講年次・学期</th>
        </tr>
          {Object.keys(data).map(key => <Lecture key={key} details={data[key]} filterClass={val}/>)}
    </table>
    </>
  )
};
